import db from "../firebase";
import { ref, get, remove, push } from "firebase/database";

const dbRef = ref(db, "/stars");

const getAllStars = () => {
  return get(dbRef);
};
const addBike = (galaxy, discoveryDate, imageUrl, name) => {
  return push(dbRef, {
  
            galaxy: galaxy,
            discoveryDate: discoveryDate,
            imageUrl: imageUrl,
            name: name
  });
};

export default {
  getAllStars,
  addBike
};
