import db from "../firebase";
import { ref, get, push } from "firebase/database";
const dbRef = ref(db, "/stars");

const getAllStars = () => {
  return get(dbRef);
};
const addStar = (galaxy, discoveryDate, imageUrl, name) => {
  return push(dbRef, {
    galaxy: galaxy,
    discoveryDate: discoveryDate,
    imageUrl: imageUrl,
    name: name
  });
};

export default{ getAllStars, addStar };
