import db from "../firebase";
import { ref, get, remove, push, update} from "firebase/database";
const dbRef = ref(db, "/stars");

export const getAllStars = () => {
  return get(dbRef);
};

export const addStar = (name, discoveryDate, imageUrl, galaxy) => {
  return push(dbRef, {
    galaxy: galaxy,
    discoveryDate: discoveryDate,
    imageUrl: imageUrl,
    name: name
  });
};
export const removeStar = (key) => {
  const dbRefStar = ref(db, `/stars/${key}`);
  return remove(dbRefStar);
};


export default  modifyStar = (key, updatedData) => {
  const dbRefStar = ref(db, `/stars/${key}`);
  return update(dbRefStar, updatedData);
};
