import { useState, useEffect} from "react";
import StarService from "../services/star.service";

function StarList() {
  const [stars, setStars] = useState([]);
  const getAllStars = () => {
    StarService.getAllStars()
      .then((items) => {
        let allStars = [];
        items.forEach(item => {
          const key = item.key;
          const data = item.val();
          allStars.push({
            key: key,
            galaxy : data.galaxy,
            discoveryDate: data.discoverydate
          });
        });
        setStars([...allStars]);
      })
     
  }


  useEffect(() => {
    getAllStars();
  }, []);

  return (
    <>
    <div class="stars-container">
            {stars.map(star => (
                <div  key={s.key}>
                <div class="fotoEstrella"><img class="fotos" src={star.imageUrl} alt={s.name} /></div>
                <div key={s.id} >
                    <h2>{s.name}</h2>
                    <p>Discovered on: {s.discoveryDate}</p>
                    <p>Galaxy: {s.galaxy}</p>
                </div>
                </div>
            ))}
        </div>

     
    </>
  );
}

export default StarList;
