import { useState, useEffect, useRef  } from "react";
import StarService from "../services/star.service";

function StarList() {
  const [stars, setStars] = useState([]);
  const refForm = useRef();

  const getAllStars = () => {
    StarService.getAllStars()
      .then((items) => {
        let allStars = [];
        items.forEach(item => {
          const key = item.key;
          const data = item.val();
          allStars.push({
            key: key,
            galaxy: data.galaxy,
            discoveryDate: data.discoverydate,
            imageUrl: data.imageUrl,
            name: data.name
          });
        });
        setStars([...allStars]);
      })
      .catch((err) => {
        console.error(err);
      });
  }

  const removeStar = (key) => {
    StarService.removeStar(key).then((res) => {
      getAllStars();
    });
  }

  const addStar = (e) => {
    e.preventDefault();
    
    const name = e.target.elements.name.value;
    const discoveryDate = e.target.elements.discoveryDate.value;
    const imageUrl = e.target.elements.imageUrl.value;
    const galaxy = e.target.elements.galaxy.value;
  
    StarService.addStar(name, discoveryDate, imageUrl, galaxy)
      .then((res) => {
        refForm.current.reset();
        setStars(oldValues => [...oldValues, { key: res.key, name, discoveryDate, imageUrl, galaxy }])
      })
      .catch((error) => {
        console.error(error);
      });
  }
  

  useEffect(() => {
    getAllStars();
  }, []);

  return (
    <>
      <div className="star-form-container">
        <form id="star-form" onSubmit={addStar} ref={refForm}>
          <input className="rounded-input" type="text" name="name" placeholder="Name" />
          <input className="rounded-input" type="text" name="discoveryDate" placeholder="Discovery date" />
          <input className="rounded-input" type="text" name="galaxy" placeholder="Galaxy" />
          <input className="rounded-input" type="file" name="image" placeholder="image" />
          <input className="rounded-input" type="submit" value="Add Star" />
        </form>
      </div>

      <div class="stars-container">
        {stars.map(star => (
          <>
            <div class="fotoEstrella"><img class="fotos" src={star.imageUrl} alt={star.name} /></div>
            <div key={star.key} >
              <h2>{star.name}</h2>
              <p>Discovered on: {star.discoveryDate}</p>
              <p>Galaxy: {star.galaxy}</p>
            </div>
          </>

        ))}
      </div>
    </>

  );
}
export default StarList;
