import db from "../firebase";
import { ref, get, remove, push } from "firebase/database";

const dbRef = ref(db, "/stars");

const getAllStars = () => {
  return get(dbRef);
};

const addStar = (brand, model) => {
  return push(dbRef, {
    brand: brand,
    model: model
  });
};

const removeStar = (key) => {
  const dbRefStar = ref(db, `/bikes/${key}`);
  return remove(dbRefStar);
};

export default {
  getAllStars,
  addStar,
  removeStar,
};
