import { useState, useEffect, useRef } from "react";
import firebase from "firebase/app";
import "firebase/database";

function StarList() {
  const [stars, setStars] = useState([]);
  const refForm = useRef();

  const getAllStars = () => {
    const database = firebase.database();
    const starsRef = database.ref("stars");

    starsRef.once("value")
      .then((snapshot) => {
        const starsData = snapshot.val();
        if (starsData) {
          const starsArray = Object.keys(starsData).map((key) => {
            return {
              key: key,
              ...starsData[key]
            };
          });
          setStars([...starsArray]);
        }
      })
      .catch((err) => {
        console.error(err);
      });
  }

  const removeStar = (key) => {
    const database = firebase.database();
    const starRef = database.ref(`stars/${key}`);

    starRef.remove()
      .then(() => {
        getAllStars();
      })
      .catch((err) => {
        console.error(err);
      });
  }

  const addStar = (e) => {
    e.preventDefault();
    const name = e.target.name.value;
    const discoveryDate = e.target.discoveryDate.value;
    const galaxy = e.target.galaxy.value;
    const imageUrl = e.target.imageUrl.value;

    const database = firebase.database();
    const starsRef = database.ref("stars");

    const newStarRef = starsRef.push();
    newStarRef.set({
      name: name,
      discoveryDate: discoveryDate,
      galaxy: galaxy,
      imageUrl: imageUrl
    })
      .then(() => {
        refForm.current.reset();
        setStars(oldValues => [...oldValues, { key: newStarRef.key, name, discoveryDate, galaxy, imageUrl }])
      })
      .catch((err) => {
        console.error(err);
      });
  }

  useEffect(() => {
    getAllStars();
  }, []);

  return (
    <>
      <div className="star-list-main-container">
        <div className="star-form-container">
          <form id="star-form" onSubmit={addStar} ref={refForm}>
            <input className="rounded-input" type="text" name="name" placeholder="Name" />
            <input className="rounded-input" type="text" name="discoveryDate" placeholder="Discovery Date" />
            <input className="rounded-input" type="text" name="galaxy" placeholder="Galaxy" />
            <input className="rounded-input" type="text" name="imageUrl" placeholder="Image URL" />
            <input className="rounded-input" type="submit" value="Add Star" />
          </form>
        </div>

        <div className="star-list">
          {stars.map((star) => (
            <div className="star-item" key={star.key}>
              <p>{star.name}</p>
              <img src={star.imageUrl} alt={star.name} />
              <FaRegTrashAlt className="delete-star" onClick={() => removeStar(star.key)} />
        </div>
      ))}
    </div>
  </div>
</>
);
}

export default StarList;
