import db from "../firebase";
import { ref, get, remove } from "firebase/database";
import { push } from "firebase/database";
const dbRef = ref(db, "/stars");

const getAllStars = () => {
  return get(dbRef);
};
const addStar = (galaxy, discoveryDate, imageUrl, name) => {
  const newStarRef = push(dbRef);
  const newStarKey = newStarRef.key;

  return set(newStarRef, {
    galaxy: galaxy,
    discoveryDate: discoveryDate,
    imageUrl: imageUrl,
    name: name
  })
  .then(() => newStarKey);
};

export default {
  getAllStars,
  addStar
};
